import {
    ADD_TO_CART,
    REMOVE_FROM_CART,
    UPDATE_QUANTITY,
    REMOVE_CART,
  } from '../actions';

  const initialState = {};

  export default function cart(state = initialState, { type, payload }) {
    let cartList = { ...state };

    switch (type) {
      case UPDATE_QUANTITY:
        cartList[payload.name].quantity += payload.number;
        break;

      case REMOVE_FROM_CART:
        delete cartList[payload.name];
        break;

      case ADD_TO_CART:
        cartList[payload.name] = { ...payload };
        break;

      case REMOVE_CART:
        cartList = {};
        break;

      default:
        break;
    }

    return cartList;
  };
