import React, { useState, useEffect } from 'react';
import { useStore, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import {addToCart, removeFromCart, updateQuantity} from '../redux/actions';
import mock from '../services/mockApi';

function Product() {
  const dispatch = useDispatch();
  const { id } = useParams();
  const [observacao, setObservacao] = useState('');
  const [quantity, setQuantity] = useState(null);
  const [produto, setProduto] = useState({});

  useEffect(() => {
    const item = mock.filter((item) => item.id === parseInt(id));
    setProduto(item[0])
  }, []);

  const handleCart = (number) => {
    if (number < 0 && !quantity) return null;
    if (!quantity) {
      dispatch(addToCart({}));
    } else if (quantity + number <= 0) dispatch(removeFromCart());
    else {
      dispatch(updateQuantity({}));
    }
    return null;
  };

  return (
    <div>
      <Header />
      <div className='container text-center'>
        <div className='row'>
          <div className='col-sm mb-4'>
            <img
              src={produto.image}
              width='300px'
              height='200px'
              alt={produto.name}
            />
          </div>
          <div className='col-sm'>
            <div className='d-flex flex-column'>
              <h5 className='mb-4'>{produto.name}</h5>
              <div className='d-flex justify-content-around mb-4'>
                Quantidade:
                <div className='quantity'>
                  <button className='btn badge badge-info' type='button' onClick={ () => handleCart(-1) }>
                    -
                  </button>
                  <span className='mx-2'>{quantity || 0}</span>
                  <button className='btn badge badge-info' type='button' onClick={ () => handleCart(1) }>
                    +
                  </button>
                </div>
              </div>

              <form>
                <div className='form-group'>
                  <label htmlFor='form'>Alguma observação?</label>
                  <textarea
                    className='form-control'
                    id='form'
                    rows='3'
                  ></textarea>
                </div>
              </form>
              <button className='btn btn-md btn-success'>Adicionar</button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Product;
