import React, { useState, useEffect } from 'react';
import { useStore, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import mock from '../services/mockApi';

function Product() {
  const { id } = useParams();
  const [observacao, setObservacao] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [produto, setProduto] = useState({});

  useEffect(() => {
    const item = mock.filter((item) => item.id === id);
    setProduto(item)
  }, [produto]);

  return (
    <div>
      <Header />
      <div className='container text-center'>
        <div className='row'>
          <div className='col-sm mb-4'>
            <img
              src='https://picsum.photos/300/200'
              width='300px'
              height='200px'
              alt='title'
            />
          </div>
          <div className='col-sm'>
            <div className='d-flex flex-column'>
              <h5 className='mb-4'>Nome do produto</h5>
              <div className='d-flex justify-content-around mb-4'>
                Quantidade:
                <div className='quantity'>
                  <button className='btn badge badge-info' type='button'>
                    -
                  </button>
                  <span className='mx-2'>0</span>
                  <button className='btn badge badge-info' type='button'>
                    +
                  </button>
                </div>
              </div>

              <form>
                <div className='form-group'>
                  <label htmlFor='form'>Alguma observação?</label>
                  <textarea
                    className='form-control'
                    id='form'
                    rows='3'
                  ></textarea>
                </div>
              </form>
              <button className='btn btn-md btn-success'>Adicionar</button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Product;
