import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeAllCart } from '../redux/actions';

function Footer() {

  const dispatch = useDispatch();
  const carrinho = useSelector(state => state.cart);

  const totalItens = (cart) => {
    const interable = Object.entries(cart)
    console.log(interable)
  }

  // const totalPrice = (cart) => {}

  const removeCartItems = () => {
    dispatch(removeAllCart());
  }

  return (
    <footer className='fixed-bottom navbar navbar-lg navbar-light bg-info p-4 d-flex'>
      <div className='container'>
        <div className='d-flex flex-column'>
          <span className="text-secondary">{totalItens(carrinho)}</span>
          <span>R$ 30,00</span>          
        </div>
        <button className='btn btn-secondary'>Finalizar pedido</button>
        <i className='bi-trash' onClick={() => removeCartItems()} style={{ fontSize: '40px' }}></i>
      </div>
    </footer>
  );
}

export default Footer;
